import {useSelector} from "react-redux";
import {getAllPosts} from "../../../redux/store";
import {Link} from "react-router-dom";

const Home = () => {

  const posts = useSelector(state => getAllPosts(state));

  return (
    <section>
      {posts.map(post => (
        <Link key={post.id} to={'/post/' + post.id}></Link>
      ))}
    </section>
  )
}

export default Home;
