import {useSelector} from "react-redux";
import {getAllPosts} from "../../../redux/store";
import {Link} from "react-router-dom";
import {Button} from "react-bootstrap";
import {clsx} from "clsx";

const Home = () => {

  const posts = useSelector(state => getAllPosts(state));

  return (
    <section className="py-4">
      <Container>
        <h2 className="mb-4">All posts</h2>

        <Row className="g-4">          {/* odstępy między kolumnami */}
          {posts.map(post => (
            <Col key={post.id} md={6} lg={4}> {/* 2‑kolumny ≥768 px, 3‑kolumny ≥992 px */}
              <Card className="h-100 shadow-sm">  {/* pełna wysokość, subtelny cień */}
                <Card.Body>
                  <Card.Title>{post.title}</Card.Title>

                  <Card.Text className="mb-1">
                    <strong>Author:</strong> {post.author}
                  </Card.Text>
                  <Card.Text className="mb-1">
                    <strong>Published:</strong> {post.publishedDate}
                  </Card.Text>
                  <Card.Text>{post.shortDescription}</Card.Text>

                  <Button as={Link} to={`/post/${post.id}`} variant="primary">
                    Read more
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  )
}

export default Home;
