import {useDispatch, useSelector} from "react-redux";
import {useState} from "react";
import {addPost, getPostById} from "../../../../redux/store";
import {Button, Container, Form} from "react-bootstrap";
import {useNavigate, useParams} from "react-router-dom";

const PostAddOrEdit = ({actionType}) => {

  const dispatch = useDispatch();
  const navigate = useNavigate();

  let [title, setTitle] = [];
  let [shortDescription, setShortDescription] = [];
  let [content, setContent] = [];
  let [publishedDate, setPublishedDate] = [];
  let [author, setAuthor] = [];

  const { id } = useParams();

  if (actionType === 'ADD') {
    [title, setTitle] = useState('');
    [shortDescription, setShortDescription] = useState('');
    [content, setContent] = useState('');
    [publishedDate, setPublishedDate] = useState('');
    [author, setAuthor] = useState('');
  } else if (actionType === 'EDIT') {
    const post = useSelector(state => getPostById(state, id));
    [title, setTitle] = useState(post.title);
    [shortDescription, setShortDescription] = useState(post.description);
    [content, setContent] = useState(post.content);
    [publishedDate, setPublishedDate] = useState(post.publishedDate);
    [author, setAuthor] = useState(post.author);
  }

  const handleAddSubmit = e => {
    e.preventDefault();
    dispatch(addPost({title, shortDescription, content, publishedDate, author}));
    navigate('/');
  };

  return (
    <Container className="py-4">
      <h2 className="mb-4">Add post</h2>
      <Form onSubmit={handleAddSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter title"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Author</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter author"
            value={author}
            onChange={e => setAuthor(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Published</Form.Label>
          <Form.Control
            type="date"
            value={publishedDate}
            onChange={e => setPublishedDate(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Short description</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            placeholder="Leave a comment here"
            value={shortDescription}
            onChange={e => setShortDescription(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-4">
          <Form.Label>Main content</Form.Label>
          <Form.Control
            as="textarea"
            rows={8}
            placeholder="Leave a comment here"
            value={content}
            onChange={e => setContent(e.target.value)}
            required
          />
        </Form.Group>

        <div className="d-flex justify-content-start">
          <Button variant="primary" type="submit">
            Add post
          </Button>
        </div>
      </Form>
    </Container>
  );
};

export default PostAddOrEdit;
