import {useSelector} from "react-redux";
import {getPostById} from "../../../../redux/store";
import {useParams} from "react-router-dom";
import {Button} from "react-bootstrap";

const PostGet = () => {

  const {id} = useParams();

  const post = useSelector(state => getPostById(state, id));

  return (
    <section className="container mt-4" style={{ maxWidth: '720px' }}>
      <div className="d-flex justify-content-between align-items-start mb-3">
        <h2 className="mb-0">{post.title}</h2>
        <div>
          <Button variant="outline-info" size="sm" className="me-2">Edit</Button>
          <Button variant="outline-danger" size="sm">Delete</Button>
        </div>
      </div>
      <p><strong>Author:</strong> {post.author}</p>
      <p><strong>Published:</strong> {post.publishedDate}</p>
      <p>{post.content}</p>
    </section>
  );
}

export default PostGet;
